def print_reward_func(m1,m2,m3,r1,r2,r3,r4):
    print("def reward_function(params): \n\n    track_width = params['track_width'] \n    distance_from_center = params['distance_from_center']\n\n    marker_1 = "+str(m1.value)+" * track_width\n    marker_2 = "+str(m2.value)+" * track_width\n    marker_3 = "+str(m3.value)+" * track_width\n\n    if distance_from_center <= marker_1:\n        reward = "+str(r1.value)+"\n    elif distance_from_center <= marker_2:\n        reward = "+str(r2.value)+"\n    elif distance_from_center <= marker_3:\n        reward = "+str(r3.value)+"\n    else:\n        reward = "+str(r4.value)+"\n\n    return float(reward)")
